INSERT INTO inventions VALUES (93, 'Suppressor', 'Глушитель (оружейный)', 'Дульное механическое устройство стрелкового оружия, ослабляющее звук выстрела и скрывающее пламя пороховых газов', 1909, '{88}', 285, 'https://en.wikipedia.org/wiki/Suppressor');
INSERT INTO inventions VALUES (94, 'Headset', 'Головная гарнитура', 'Аксессуар систем связи, представляющий собой комбинацию головных телефонов с микрофоном', 1910, '{89}', 355, 'https://en.wikipedia.org/wiki/Headset_(audio)');
INSERT INTO inventions VALUES (95, 'Automobile self starter', 'Пусковая система двигателя внутреннего сгорания', 'Система, которая создаёт начальный вращающий моменти двигателю внутреннего сгорания для его старта', 1911, '{90}', 114, 'https://en.wikipedia.org/wiki/Starter_(engine)');
